package growmingvariables;

public class LogicalOperaters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Boolean b1 = true;
Boolean b2 = false ;
System.out.println("b1&&b2:"+(b1&&b2));
System.out.println("b1||b2:"+(b1||b2));
System.out.println("!(b1&&b2):"+!(b1&&b2));
System.out.println("!(b1||b2):"+!(b1||b2));
	}

}
